    private void buttonVerificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonVerificarActionPerformed
        // TODO add your handling code here:
        if (textFrase.getText().trim().isEmpty()){
            areaTexto.setText("Obrigatório");
            return;
        }
        String original = textFrase.getText();
        for (int i=0; i < original.length(); i++){
            pilha.pushStack(original.charAt(i)+"");
        }
        String invertida = "";
        while (!pilha.isEmptyStack()){
            invertida = invertida + pilha.topStack();
            pilha.popStack();
        }
        if (original.equals(invertida)){
            areaTexto.setText(original + "\t" 
                            + invertida + " são iguais");
        }else{
            areaTexto.setText(original + "\t" 
                            + invertida + " são diferentes");
        }
    }//GEN-LAST:event_buttonVerificarActionPerformed

